import React, { useState } from 'react';
import axios from 'axios';

const Auth = ({ setPlayerId }) => {
  const [username, setUsername] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [message, setMessage] = useState('');

  const handleSubmit = async () => {
    try {
      const endpoint = isLogin ? '/api/player/login' : '/api/player/register';
      const res = await axios.post(`https://crypto-crash-game-zohl.onrender.com${endpoint}`, { username });
      setPlayerId(res.data._id);
      setMessage('✅ Success!');
    } catch (err) {
      setMessage('❌ Error. Try a different username.');
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <input
        placeholder="Enter username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      /><br />
      <button onClick={handleSubmit}>{isLogin ? 'Login' : 'Register'}</button>
      <p>{message}</p>
      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Need to register?' : 'Already have an account?'}
      </button>
    </div>
  );
};

export default Auth;
