import React, { useEffect, useState } from "react";
import socket from "./socket";
import { registerUser, loginUser, placeBet } from "./api";

function App() {
  const [username, setUsername] = useState("");
  const [user, setUser] = useState(null);
  const [multiplier, setMultiplier] = useState(1.0);
  const [crashed, setCrashed] = useState(false);
  const [error, setError] = useState("");
  const [isRegisterMode, setIsRegisterMode] = useState(true);
  const [currency, setCurrency] = useState("BTC");
  const [usdAmount, setUsdAmount] = useState(10);

  useEffect(() => {
    socket.on("multiplier_update", (data) => {
      setMultiplier(data.multiplier);
    });

    socket.on("crash", () => {
      setCrashed(true);
    });

    return () => {
      socket.off("multiplier_update");
      socket.off("crash");
    };
  }, []);

  const handleAuth = async () => {
    setError("");
    try {
      const res = isRegisterMode
        ? await registerUser(username)
        : await loginUser(username);
      setUser(res.player);
      setUsername("");
    } catch (err) {
      setError(err.response?.data?.error || "An error occurred");
    }
  };

  const handlePlaceBet = async () => {
    if (!user) return;

    const data = {
      playerId: user.id,
      usdAmount: parseFloat(usdAmount),
      currency,
    };

    try {
      await placeBet(data);
      socket.emit("place_bet", data);
    } catch (err) {
      setError(err.response?.data?.error || "Failed to place bet");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px", fontFamily: "Arial" }}>
      <h1>Crypto Crash Game</h1>

      {!user ? (
        <>
          <h3>{isRegisterMode ? "Register" : "Login"} to Play</h3>
          <input
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
          <br />
          <button onClick={handleAuth}>
            {isRegisterMode ? "Register" : "Login"}
          </button>
          <br />
          <small>
            {isRegisterMode ? "Already have an account?" : "New user?"}{" "}
            <button onClick={() => setIsRegisterMode(!isRegisterMode)}>
              {isRegisterMode ? "Login here" : "Register here"}
            </button>
          </small>
          <br />
          {error && <p style={{ color: "red" }}>{error}</p>}
        </>
      ) : (
        <>
          <h2>Welcome, {user.username}</h2>
          <p>Wallets:</p>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li>BTC: {user.wallets.BTC}</li>
            <li>ETH: {user.wallets.ETH}</li>
          </ul>

          <h3>Multiplier: {multiplier.toFixed(2)}x</h3>
          <h3>{crashed ? "ðŸ’¥ Crashed!" : "ðŸŸ¢ Live"}</h3>

          <div>
            <label>
              Amount (USD):
              <input
                type="number"
                value={usdAmount}
                onChange={(e) => setUsdAmount(e.target.value)}
              />
            </label>
            <br />
            <label>
              Currency:
              <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
                <option value="BTC">BTC</option>
                <option value="ETH">ETH</option>
              </select>
            </label>
            <br />
            <button onClick={handlePlaceBet}>Place Bet</button>
          </div>

          {error && <p style={{ color: "red" }}>{error}</p>}
        </>
      )}
    </div>
  );
}

export default App;
