import React, { useEffect, useState } from "react";
import socket from "./socket";
import { registerUser, placeBet } from "./api";

function App() {
  const [username, setUsername] = useState("");
  const [user, setUser] = useState(null);
  const [multiplier, setMultiplier] = useState(1.0);
  const [crashed, setCrashed] = useState(false);

  useEffect(() => {
    socket.on("multiplier_update", (data) => {
      setMultiplier(data.multiplier);
    });

    socket.on("crash", () => {
      setCrashed(true);
    });

    return () => {
      socket.off("multiplier_update");
      socket.off("crash");
    };
  }, []);

  const handleRegister = async () => {
    const res = await registerUser(username);
    setUser(res);
  };

  const handlePlaceBet = async () => {
    const data = {
      playerId: user._id,
      usdAmount: 10,
      currency: "BTC",
    };
    await placeBet(data);
    socket.emit("place_bet", data);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      {!user ? (
        <>
          <input value={username} onChange={(e) => setUsername(e.target.value)} />
          <button onClick={handleRegister}>Register</button>
        </>
      ) : (
        <>
          <h2>Welcome, {user.username}</h2>
          <h3>Multiplier: {multiplier.toFixed(2)}x</h3>
          <h3>{crashed ? "ðŸ’¥ Crashed!" : "ðŸŸ¢ Live"}</h3>
          <button onClick={handlePlaceBet}>Place Bet ($10)</button>
        </>
      )}
    </div>
  );
}

export default App;
