import React, { useEffect, useState } from "react";
import socket from "./socket";
import { registerUser, placeBet, withdrawAmount } from "./api";

function App() {
  const [username, setUsername] = useState("");
  const [user, setUser] = useState(null);
  const [multiplier, setMultiplier] = useState(1.0);
  const [crashed, setCrashed] = useState(false);
  const [error, setError] = useState("");
  const [currency, setCurrency] = useState("BTC");
  const [usdAmount, setUsdAmount] = useState(10);
  const [withdrawAmountValue, setWithdrawAmountValue] = useState(0);

  useEffect(() => {
    socket.on("tick", (data) => {
      const value = parseFloat(data.multiplier);
      setMultiplier(isNaN(value) ? 1.0 : value);
    });

    socket.on("crash", () => {
      setCrashed(true);
    });

    return () => {
      socket.off("tick");
      socket.off("crash");
    };
  }, []);

  const handleRegister = async () => {
    setError("");
    try {
      const res = await registerUser(username);
      setUser(res.player);
      setUsername("");
    } catch (err) {
      setError(err.response?.data?.error || "An error occurred");
    }
  };

  const handlePlaceBet = async () => {
    if (!user) return alert("Register or login first!");
    try {
      const data = {
        playerId: user.id,
        usdAmount: parseFloat(usdAmount),
        currency,
        price: 30000,
      };
      await placeBet(data);
      socket.emit("place_bet", data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "Bet failed");
    }
  };

  const handleWithdraw = async () => {
    if (!user) return alert("Register or login first!");
    try {
      const res = await withdrawAmount({
        playerId: user.id,
        amount: parseFloat(withdrawAmountValue),
        currency,
      });
      alert("Withdraw successful!");
      setUser((prev) => ({
        ...prev,
        wallets: res.newBalance,
      }));
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || "Withdraw failed");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px", fontFamily: "Arial" }}>
      <h1>Crypto Crash Game</h1>

      {!user ? (
        <>
          <input
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
          <button onClick={handleRegister}>Register</button>
          <br />
          {error && <p style={{ color: "red" }}>{error}</p>}
        </>
      ) : (
        <>
          <h2>Welcome, {user.username}</h2>
          <p>Wallets:</p>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li>BTC: {user.wallets.BTC}</li>
            <li>ETH: {user.wallets.ETH}</li>
          </ul>

          <h3>Multiplier: {Number(multiplier).toFixed(2)}x</h3>
          <h3>{crashed ? "ðŸ’¥ Crashed!" : "ðŸŸ¢ Live"}</h3>

          <div>
            <h4>Place Bet</h4>
            <input
              type="number"
              value={usdAmount}
              onChange={(e) => setUsdAmount(e.target.value)}
              placeholder="USD Amount"
            />
            <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
              <option value="BTC">BTC</option>
              <option value="ETH">ETH</option>
            </select>
            <button onClick={handlePlaceBet}>Place Bet</button>
          </div>

          <div style={{ marginTop: "20px" }}>
            <h4>Withdraw</h4>
            <input
              type="number"
              value={withdrawAmountValue}
              onChange={(e) => setWithdrawAmountValue(e.target.value)}
              placeholder="Crypto Amount"
            />
            <button onClick={handleWithdraw}>Withdraw</button>
          </div>

          {error && <p style={{ color: "red" }}>{error}</p>}
        </>
      )}
    </div>
  );
}

export default App;
